import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {

  const [selectedYear, setSelectedYear] = useState('');

  const onSelectYearHandler = (event) => {
    // event.preventDefault();
    const expenseYear = event.value;

    props.onSelectExpenseYear(expenseYear);
    setSelectedYear('');
};

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option onSelect={onSelectYearHandler} value='2022'>2022</option>
          <option onSelect={onSelectYearHandler} value='2021'>2021</option>
          <option onSelect={onSelectYearHandler} value='2020'>2020</option>
          <option onSelect={onSelectYearHandler} value='2019'>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
